package week5MondayRecap;

public class ForLoop2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		// Write a program to calculate all numbers between 1 to 5

		int sum = 0;
		for (int i = 1; i <= 5; i++) {
			sum = sum + i;

		}
		System.out.println(sum);

	}

}
